//Two major functions:
//1. Hides the page table of contents (located in the sectionblur div) when it exceeds toclength; preventing the toc being too short (useless) or obscuring margin notes which can't be scrolled through
//2. Strips the R code chunks that get generated by blogdown

var toclength = 7

document.onreadystatechange = function() {
    if (document.readyState === "interactive") {
		if (document.getElementById("TableOfContents").getElementsByTagName("li").length < toclength) {
            document.getElementsByClassName('sectionblur')[0].style.display = "none";
       	}
		var rblock = document.getElementsByClassName("language-r");
		for(var e = 0; e <= rblock.length; e++){
		  rblock[e].parentNode.style.display = "none"; 
		}
    }
}